import React, { useEffect, useState } from 'react'
//Carrusel
import SlideShow from 'react-image-show';


const Index = props => {
  const [ state , setState ] = useState({
    loaded: false
  })
  const { loaded } = state;
  useEffect(() => {
    setTimeout(() => this.setState({ loaded: true }), 2000)
    
  }, 
  [])

  const urlArray = [
    "https://cdn.pixabay.com/photo/2018/09/30/19/03/railway-station-3714297__340.jpg",
    "https://i.ytimg.com/vi/HEkpbJYyv6k/maxresdefault.jpg"
  ]
  return (
    <>
      <h1>HomePage</h1>
      {
        loaded && (<SlideShow
        images={urlArray}
        width="920px"
        imagesWidth="800px"
        imagesHeight="450px"
        imagesHeightMobile="56vw"
        thumbnailsWidth="920px"
        thumbnailsHeight="12vw"
        indicators thumbnails fixedImagesHeight
      />)
      }
    </>
  )
}


Index.propTypes = {

}

export default Index
